ymaps.ready(init);

function init() {

    // Создание экземпляра карты.
    var myMap = new ymaps.Map('contact__markets', {
            center: [50.443705, 30.530946],
            zoom: 14,
            controls: ['zoomControl', 'fullscreenControl', 'routeButtonControl']
        }, {
            searchControlProvider: 'yandex#search'
        }),
        // Контейнер для меню.
        menu = $('<select id="select"/>');
        for (var i = 0, l = groups.length; i < l; i++) {
            var submenu = $(`<table class="contact__table contact__table_sm" id="tab${i+1}" cellspacing="0" cellpadding="0">`);
            submenu.appendTo($('#tabs'));
            createMenuGroup(groups[i]);
        }

    function createMenuGroup (group) {
        // Пункт меню.
        var menuItem = $(`<option value="tab${i+1}">` + group.name + `</option>`),
        // Коллекция для геообъектов группы.
        collection = new ymaps.GeoObjectCollection(null, { preset: group.style });

        // Добавляем коллекцию на карту.
        myMap.geoObjects.add(collection);
        // Добавляем подменю.
        menuItem
            // Добавляем пункт в меню.
            .appendTo(menu)
        for (var j = 0, m = group.items.length; j < m; j++) {
            createSubMenu(group.items[j], collection);
        }
    }

    function createSubMenu (item, collection) {
        // Контейнер для

        // Пункт подменю.
        var submenuItem = $('<tr><td><b>' + item.city + '</b><br>' + item.name + '</td><td>' + item.address  + '</td><td><a class="show-place" href="#contact__markets">Показать на карте</a></td><td>' + item.phone  + '</td><td>' + item.mail + '</td></tr>'),
        // Создаем метку.
            placemark = new ymaps.Placemark(item.center, {
              balloonContent: item.name + '<br>' + item.city + '&nbsp' + item.address + '<br>' + item.phone + '<br>' + item.mail},
              {
                iconLayout: 'default#image',
                 // Своё изображение иконки метки.
                 iconImageHref: 'img/placemark.png',
                 // Размеры метки.
                 iconImageSize: [30, 42],
                 // Смещение левого верхнего угла иконки относительно
                 // её "ножки" (точки привязки).
                 iconImageOffset: [-15, -38]
              });
            //submenu = $(`<table class="contact__table contact__table_sm" id="tab" cellspacing="0" cellpadding="0">`)

        // Добавляем метку в коллекцию.
        collection.add(placemark);
        // Добавляем пункт в подменю.
        submenuItem
            .appendTo(submenu)
            // При клике по пункту подменю открываем/закрываем баллун у метки.
            .find('a.show-place')
            .bind('click', function () {
                if (!placemark.balloon.isOpen()) {
                    placemark.balloon.open();
                } else {
                    placemark.balloon.close();
                }
                var idPlace  = $(this).attr('href'),
                //узнаем высоту от начала страницы до блока на который ссылается якорь
                top = $(idPlace).offset().top;
                //анимируем переход на расстояние - top за 1500 мс
                $('body,html').animate({scrollTop: top - 80}, 1500);
                return false;
            });
    }

    // Добавляем меню в тэг BODY.
    menu.appendTo($('.contact__country'));
    // Выставляем масштаб карты чтобы были видны все группы.
    myMap.setBounds(myMap.geoObjects.getBounds());
    onload=tabs;
}
