function productTabs(){
  let prodTabsBlock = document.querySelector('#product__tabs');
  let prodTabs = prodTabsBlock.querySelectorAll('.product__tabs__item');
  let prodImgs = document.querySelector('#product__img');
  for(let i = 0; prodTabs.length > i; i++){
    prodTabs[i].addEventListener('click', function(e) {
      prodTabsBlock.querySelector('.active').classList.remove('active');
      let elem = e.target;
      elem.classList.add('active');
      let elemId = elem.getAttribute("value");
      prodImgs.querySelector('.show').classList.remove('show');
      prodImgs.querySelector(`#${elemId}`).classList.add('show');
    })
  }
}
productTabs();
